"use strict";const block=document.querySelector(".block"),img=document.getElementsByClassName("img");for(let i=0;i<100;i++)block.innerHTML+='<div class="block-element"><picture><source srcset="img/f2.webp" type="image/webp"><img class="img" src="img/f2.jpg" alt=""></picture></div>';const blockEl=document.getElementsByClassName("block-element");function backAnim(){let e=block.getBoundingClientRect();for(let t=0;t<100;t++){let l=2+5*Math.random();blockEl[t].style.animationDuration=2+l+"s",blockEl[t].style.animationDelay=+l+"s";let s=blockEl[t].getBoundingClientRect(),c=-s.left+e.left+"px",o=-s.top+e.top+"px";img[t].style.width=block.offsetWidth+"px",img[t].style.height=block.offsetHeight+"px",img[t].style.left=c,img[t].style.top=o}}backAnim(),backAnim(),window.addEventListener("resize",()=>{block.classList.remove("startAni"),backAnim(),setTimeout(()=>{block.classList.add("startAni")},0)});